// Service Worker for Firebase Cloud Messaging
// This file must be in the web root directory
// NOTE: Firebase config is loaded from firebase-config.js (generated at build time)
// This prevents hardcoding sensitive values in the repository

importScripts('https://www.gstatic.com/firebasejs/10.7.1/firebase-app-compat.js');
importScripts('https://www.gstatic.com/firebasejs/10.7.1/firebase-messaging-compat.js');

// Load Firebase config from external file (generated at build time)
// This file should be generated by a build script and NOT committed to git
let firebaseConfig = null;

// Try to load config from external file
try {
  importScripts('./firebase-config.js');
  // firebase-config.js should define: self.firebaseConfig = { ... }
  if (typeof self.firebaseConfig !== 'undefined') {
    firebaseConfig = self.firebaseConfig;
  }
} catch (e) {
  console.warn('[firebase-messaging-sw.js] Could not load firebase-config.js:', e);
}

// Fallback: Try to get config from main app via postMessage
self.addEventListener('message', (event) => {
  if (event.data && event.data.type === 'FIREBASE_CONFIG') {
    firebaseConfig = event.data.config;
    initializeFirebase();
  }
});

// Initialize Firebase
function initializeFirebase() {
  if (!firebaseConfig) {
    console.error('[firebase-messaging-sw.js] Firebase config not available. Service worker may not work correctly.');
    console.error('[firebase-messaging-sw.js] Please ensure firebase-config.js exists or config is provided via postMessage.');
    return;
  }
  
  try {
    firebase.initializeApp(firebaseConfig);
    console.log('[firebase-messaging-sw.js] Firebase initialized successfully');
  } catch (e) {
    console.error('[firebase-messaging-sw.js] Failed to initialize Firebase:', e);
  }
}

// Request config from main app on startup
self.addEventListener('install', (event) => {
  event.waitUntil(
    self.clients.matchAll().then((clients) => {
      clients.forEach((client) => {
        client.postMessage({ type: 'REQUEST_FIREBASE_CONFIG' });
      });
    })
  );
  
  // Initialize after a short delay to allow config to be received
  setTimeout(() => {
    if (!firebaseConfig) {
      initializeFirebase();
    }
  }, 1000);
});

// Initialize immediately if config is already available
if (firebaseConfig) {
  initializeFirebase();
}

// Retrieve an instance of Firebase Messaging
let messaging = null;
try {
  messaging = firebase.messaging();
} catch (e) {
  console.error('[firebase-messaging-sw.js] Failed to get messaging instance:', e);
}

// Handle background messages
if (messaging) {
  messaging.onBackgroundMessage((payload) => {
    console.log('[firebase-messaging-sw.js] Received background message ', payload);
    
    const notificationTitle = payload.notification?.title || 'New Notification';
    const notificationOptions = {
      body: payload.notification?.body || '',
      icon: '/icons/Icon-192.png',
      badge: '/icons/Icon-192.png',
      tag: payload.data?.notificationId || 'default',
      requireInteraction: false,
      data: payload.data || {},
    };

    return self.registration.showNotification(notificationTitle, notificationOptions);
  });
}

// Handle notification clicks
self.addEventListener('notificationclick', (event) => {
  console.log('[firebase-messaging-sw.js] Notification click received.');
  
  event.notification.close();
  
  // Handle navigation based on notification data
  const data = event.notification.data;
  if (data && data.chatRoomId) {
    event.waitUntil(
      clients.openWindow(`/chat?roomId=${data.chatRoomId}`)
    );
  } else if (data && data.bookingId) {
    event.waitUntil(
      clients.openWindow(`/myBookings?bookingId=${data.bookingId}`)
    );
  } else {
    // Default: focus or open the app
    event.waitUntil(
      clients.matchAll({ type: 'window', includeUncontrolled: true }).then((clientList) => {
        if (clientList.length > 0) {
          return clientList[0].focus();
        }
        return clients.openWindow('/');
      })
    );
  }
});
